mapper.select:
select * from activity where 1=1
 
     AND instr(address,#{school}) 
 
 
  	AND type = #{type}
 
 order by z_index desc, create_time desc limit #{start}, #{count};

  	 
		select * from activity where 1=1
		  
		  
		    AND date(start_date) <= #{search.date,jdbcType=TIMESTAMP}
		    AND date(end_date) >= #{search.date,jdbcType=TIMESTAMP}
		   
		  
		  
		    AND instr(address,#{search.location})  
		  
		  
		    AND instr(name,#{search.keywords})  
		  
		  order by create_time desc limit #{search.start}, #{search.count}
